import React, { useState } from "react";
import { useEffect } from "react";
import { CustomOverlayMap, Map, MapMarker } from "react-kakao-maps-sdk";

const { kakao } = window;

const MapPage = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [locate, setlocate] = useState(0, 0);
  const markerPosition = {
    lat: 33.450701,
    lng: 126.570667,
  };
  if (navigator.geolocation) {
    // GeoLocation을 이용해서 접속 위치를 얻어옵니다
    navigator.geolocation.getCurrentPosition(function (position) {
      const lat = position.coords.latitude, // 위도
        lon = position.coords.longitude; // 경도
      console.log(lat, lon);
      setlocate(lat, lon);
      console.log(locate); // 마커가 표시될 위치를 geolocation으로 얻어온 좌표로 생성합니다
    });
  }

  return (
    <>
      <div className="h-14 flex items-center justify-center relative bg-blue-400">
        <button className=" absolute left-2 text-white">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-8 w-8"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </button>
        <h1 className=" text-white text-5xl ">Market</h1>
      </div>
      <div className="flex justify-center items-end h-[100vh]">
        <Map // 지도를 표시할 Container
          id={`map`}
          center={{
            // 지도의 중심좌표
            lat: 33.451475,
            lng: 126.570528,
          }}
          style={{
            // 지도의 크기
            width: "100%",
            height: "450px",
          }}
          level={3} // 지도의 확대 레벨
        >
          <MapMarker position={locate} onClick={() => setIsOpen(true)} />
          {isOpen && (
            <CustomOverlayMap position={locate}>
              <div className="wrap">
                <div className="info">
                  <div className="title">
                    카카오 스페이스닷원
                    <div
                      className="close"
                      onClick={() => setIsOpen(false)}
                      title="닫기"
                    ></div>
                  </div>
                  <div className="body">
                    <div className="img">
                      <img
                        src="//t1.daumcdn.net/thumb/C84x76/?fname=http://t1.daumcdn.net/cfile/2170353A51B82DE005"
                        width="73"
                        height="70"
                        alt="카카오 스페이스닷원"
                      />
                    </div>
                    <div className="desc">
                      <div className="ellipsis">
                        제주특별자치도 제주시 첨단로 242
                      </div>
                      <div className="jibun ellipsis">
                        (우) 63309 (지번) 영평동 2181
                      </div>
                      <div>
                        <a
                          href="https://www.kakaocorp.com/main"
                          target="_blank"
                          className="link"
                          rel="noreferrer"
                        >
                          홈페이지
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              ;
            </CustomOverlayMap>
          )}
        </Map>
      </div>
    </>
  );
};

export default MapPage;
